ResearchTopicDAO.gTopicList= SELECT a.research_topic_id, a.research_month, a.research_topic, a.status, a.remarks, a.CREATEDDATE, a.ApprovedDATE, a.approver,c.USRFULLNAME createdBy, b.status_name FROM research_topic a LEFT JOIN common_status b ON a.status = b.status_id LEFT JOIN sa_user c ON c.usrusername = a.createdby WHERE (:status is null or a.STATUS = :status)  AND (:userName IS NULL OR a.CREATEDBY = :userName)
ResearchTopicDAO.findTopic= SELECT a.research_topic_id, a.research_month, a.research_topic, a.status, a.remarks, a.CREATEDDATE, a.ApprovedDATE, a.approver,c.USRFULLNAME createdBy, b.status_name FROM research_topic a LEFT JOIN common_status b ON a.status = b.status_id LEFT JOIN sa_user c ON c.usrusername = a.createdby WHERE a.research_month = :research_month AND (:userName IS NULL OR a.CREATEDBY = :userName)