ResearchTopicDAO.gTopicList= SELECT a.research_topic_id, a.research_month, a.research_topic, a.objectives,a.status, a.remarks, a.CREATEDDATE, a.ApprovedDATE, a.approver,c.USRFULLNAME createdBy, b.status_name, E.position_title, c.USREMPLOYEEID as employee_id FROM research_topic a LEFT JOIN common_status b ON a.status = b.status_id LEFT JOIN sa_user c ON c.usrusername = a.createdby LEFT JOIN sa_employee_details E on E.employee_id = c.USREMPLOYEEID  WHERE (:status is null or a.STATUS = :status)  AND (:userName IS NULL OR a.CREATEDBY = :userName) and (:research_month is null or a.research_month=:research_month) order by a.CREATEDDATE desc
ResearchTopicDAO.findTopic= SELECT a.research_topic_id, a.research_month, a.research_topic, a.objectives, a.status, a.remarks, a.CREATEDDATE, a.ApprovedDATE, a.approver,c.USRFULLNAME createdBy, b.status_name FROM research_topic a LEFT JOIN common_status b ON a.status = b.status_id LEFT JOIN sa_user c ON c.usrusername = a.createdby WHERE a.research_month = :research_month AND (:userName IS NULL OR a.CREATEDBY = :userName)